// 容器组件 用来包裹 UI组件
//所以要引入UI组件

import counter from '../components/counter'
import {connect} from "react-redux"


function demo(state){
    console.log(state);
    return { count :state}
}

function demo2(dispatch){
    console.log(dispatch);
    return {
        dispatch:dispatch
    }
}


export default connect(demo,demo2)(counter) 

// connect 连接UI 组件，生成一个高级容器组件

//demo 和demo2 相当于向子UI组件，转递props值
