let initstates =0
export default function operaCount( prestates=initstates,action){
    //根据action中的type和data，决定应该去怎么操作状态
     let {type,data}=action
     switch (type) {
         case "increment":
            prestates+=data
            return prestates
  
         default:
             return prestates
     }
   
}
