// 容器组件 用来包裹 UI组件
//所以要引入UI组件

import counter from '../components/counter'
import { connect } from "react-redux"

import { incrementadd, incrementsub } from '../redux/action'


//完整写法
// function mapStatetoProps(state){
//     console.log(state);
//     return { count :state}
// }
//简写
// mapStatetoProps=state=>({ count :state})

//完整写法
// function mapdispatchToProps(dispatch) {
//     console.log(dispatch);
//     return {
//         increment: (value) => { dispatch(incrementadd(value)) },
//         decrement: (value) => { dispatch(incrementsub(value)) }
//     }
// }
//简写
mapdispatchToProps = dispatch => (
    {
        increment: (value) => { dispatch(incrementadd(value)) },
        decrement: (value) => { dispatch(incrementsub(value)) }
    }
)



//完整写法
// export default connect(mapStatetoProps,mapdispatchToProps)(counter) 
//简写
export default connect(state => ({ count: state }), mapdispatchToProps)(counter)

// connect 连接UI 组件，生成一个高级容器组件

//demo 和demo2 相当于向子UI组件，转递props值
